import React, { useState, useEffect } from "react";
// import "./style.css";
// import "./script.js";

const HackerTypeClone = () => {
  const [sourceCode, setSourceCode] = useState("");
  const [startIndex, setStartIndex] = useState(0);
  const [endIndex, setEndIndex] = useState(0);
  const [cursorChar, setCursorChar] = useState("|");

  useEffect(() => {
    getFileContents();
    window.setInterval(updateCursor, 500);
  }, []);

  const getFileContents = () => {
    var rawFile = new XMLHttpRequest();
    rawFile.onreadystatechange = function () {
      if (rawFile.readyState === 4) {
        if (rawFile.status === 200 || rawFile.status === 0) {
          setSourceCode(rawFile.responseText);
        }
      }
    };
    rawFile.open("GET", "code.txt", true);
    rawFile.send();
  };

  const getElements = () => {
    const sourceContainer = document.getElementById("container");
    const sourceElement = document.getElementById("source");
    return { sourceContainer, sourceElement };
  };

  const updateCursor = () => {
    const { sourceElement } = getElements();
    const text = sourceElement.textContent;
    const lastChar = text.charAt(text.length - 1);
    if (lastChar === cursorChar) {
      sourceElement.textContent = text.substring(0, text.length - 1);
    } else {
      sourceElement.textContent += cursorChar;
    }
  };

  const update = () => {
    const { sourceElement, sourceContainer } = getElements();
    setEndIndex(endIndex + 5);
    sourceElement.textContent = sourceCode.substring(startIndex, endIndex);
    window.scrollTo(0, sourceContainer.scrollHeight);
  };

  return (
    <div id="container">
      <p id="source"></p>
    </div>
  );
};

export default HackerTypeClone;
